package com.class02;

public class InClassTask {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double num1=5.99;
		double num2=10.99;
		
		double sum=num1+num2;
		double sub=num1-num2;
		double mul=num1*num2;
		double div=num1/num2;
		
		System.out.println("The addition of 2 numbers "+num1+" and "+num2+" is equal to "+sum);
		System.out.println("The subtraction of 2 numbers "+num1+" and "+num2+" is equal to "+sub);
		System.out.println("The multiplaction of 2 numbers "+num1+" and "+num2+" is equal to "+mul);
		System.out.println("The division of 2 numbers "+num1+" and "+num2+" is equal to "+div);
		

		int width=5;
		int height=8;
		int total=width+height;
		int area=width*height;
		
		System.out.println("The perimeter of a rectangle with width "+width+" and height "+height+" is equal to "+total+" and the area is "+area);
		
		double num=3.9;
		double square=num*num;
		
		System.out.println("The square of the "+num+" is "+square);
		
		
		

	}

}
