package com.class15;

public class TrimDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Trims empty spaces
		
		String name="           Nelson ";
		System.out.println(name);
		
		System.out.println(name.trim());
		
		System.out.println("-----------------");
		
		String name1="           Nelson    *  ";
		System.out.println(name1);
		
		System.out.println(name1.trim());

	}

}
